mapper.insert:
    insert into spring5_member (
		memberid
		, memberpw
		, membername
		, email
		, phone
		, address
	) 
    values (
		#{memberid}
		, #{memberpw}
		, #{membername}
		, #{email}
		, #{phone}
		, #{address}
	)

mapper.update:
update 
	spring5_member
set
	membername = #{membername}
	, email = #{email}
	, phone = #{phone}
	, address = #{address}

	, memberpw = #{memberpw}

where
	memberid = #{memberid}
